INSERT INTO "ExamQuestion" (id, createdate, deletedate, deleteflag, updatedate, attachment, ischanged, numberofquestion, question, questionanswertype, questionid, questiontype, exammodel_id, parentquestion_id) VALUES (135, NULL, NULL, false, NULL, NULL, NULL, 5, '<p>&Aacute;nh nắng dịu d&agrave;ng mặt trời buổi s&aacute;ng m&ugrave;a gặt mơn trớn cảnh vật bao la của đồng qu&ecirc;.</p><p>Trong một thửa ruộng v&agrave;o ối, bảy c&ocirc; g&aacute;i qu&ecirc; đang lom khom gặt từng lượm l&uacute;a v&agrave;ng thơm phưng phức. Đầu họ đội n&oacute;n l&agrave; bu&ocirc;n m&agrave;u trắng v&agrave;ng. Họ kh&uacute;c kh&iacute;ch cười v&agrave; thỉnh thoảng lại đứng ngay lưng nh&igrave;n quanh như sung sướng.</p><p>Lưỡi h&aacute;i cắt v&agrave;o cọng rạ, tiếng nghe r&egrave;n rẹt. Tiếng th&acirc;n l&uacute;a cọ v&agrave;o nhau r&agrave;o r&agrave;o. M&ugrave;i thơm man m&aacute;c của l&uacute;a, m&ugrave;i ngọt ng&agrave;o của tr&aacute;i ch&iacute;n tr&ecirc;n c&acirc;y ngang nhi&ecirc;n đứng sừng sững ở bờ mẫu, m&ugrave;i nhạt nhạt của nước ruộng l&agrave;m họ như say sưa với sự l&agrave;m việc.</p><p>Sau lưng bảy c&ocirc; g&aacute;i qu&ecirc; ấy những b&oacute; l&uacute;a sắp th&agrave;nh h&agrave;ng d&agrave;i nằm tr&ecirc;n gốc rạ. Hai người đ&agrave;n &ocirc;ng lực lưỡng m&igrave;nh trần, quần vắt, đầu vấn khăn đen, đang g&ograve; lưng buộc l&uacute;a th&agrave;nh b&oacute; để chiều nay tr&acirc;u cộ về s&acirc;n đạp. V&agrave;i đứa trẻ quần ph&egrave;n, &aacute;o cộc mốc, đang chậm r&atilde;i bước sau người gặt, lom khom m&oacute;t từng b&ocirc;ng l&uacute;a đổ.</p><p>Trong nắng, phơn phớt ban mai, tiếng chim r&iacute;u r&iacute;t, gi&agrave;nh ăn tr&aacute;i c&acirc;y h&ograve;a lẫn với tiếng động cảu những người tận tụy cho đồng qu&ecirc;. Một luồng gi&oacute; m&aacute;t lượn qua. Mấy b&ocirc;ng l&uacute;a nặng trĩu gợn theo chiều gi&oacute; như những l&agrave;n s&oacute;ng uốn m&igrave;nh chạy tr&ecirc;n biển bao la v&agrave;ng.</p>', 1, 18, 2, 29, NULL);
