INSERT INTO "ExamQuestion" (id, createdate, deletedate, deleteflag, updatedate, attachment, ischanged, numberofquestion, question, questionanswertype, questionid, questiontype, exammodel_id, parentquestion_id) VALUES (141, NULL, NULL, false, NULL, NULL, NULL, 5, '<p>Buổi sớm nắng s&aacute;ng. Những c&aacute;nh buồm n&acirc;u tr&ecirc;n biển được nắng chiếu v&agrave;o hồng rực l&ecirc;n như đ&agrave;n bướm m&uacute;a lượn giữa trời xanh.</p><p>Lại đến một buổi chiều gi&oacute; m&ugrave;a đ&ocirc;ng bắc vừa dừng. Biển lặng đỏ đục, đầy như m&acirc;m b&aacute;nh đ&uacute;c, lo&aacute;ng tho&aacute;ng những con thuyền như những hạt lạc ai đem rắc l&ecirc;n tr&ecirc;n.</p><p>Rồi một ng&agrave;y mưa r&agrave;o. Mưa dăng dăng bốn ph&iacute;a. C&oacute; những qu&atilde;ng nắng xuy&ecirc;n xuống biển &oacute;ng &aacute;nh đủ m&agrave;u: xanh l&aacute; mạ, t&iacute;m phớt, hồng, xanh biếc . C&oacute; qu&atilde;ng biển th&acirc;m x&igrave;, nặng trịch. Những c&aacute;nh buồm ra khỏi cơn mưa, ướt đẫm thẫm lại, khoẻ nhẹ, bồi hồi, như ngực b&aacute;c n&ocirc;ng d&acirc;n c&agrave;y xong ruộng về bị ướt.</p><p>C&oacute; buổi sớm nắng mờ, biển bốc hơi nước, kh&ocirc;ng nom thấy n&uacute;i xa, chỉ thấy một m&agrave;u trắng đục. Kh&ocirc;ng c&oacute; thuyền, kh&ocirc;ng c&oacute; s&oacute;ng, kh&ocirc;ng c&oacute; m&acirc;y, kh&ocirc;ng c&oacute; sắc biếc của da trời. &nbsp; &nbsp; &nbsp; Một buổi chiều lạnh, nắng tắt sớm. Những n&uacute;i xa lam nhạt pha m&agrave;u trắng sữa. Kh&ocirc;ng c&oacute; gi&oacute; m&agrave; s&oacute;ng vẫn đổ đều đều, r&igrave; rầm. Nước biển d&acirc;ng đầy, qu&aacute;nh đặc như một m&agrave;u bạc trắng, lăn tăn như bột phấn tr&ecirc;n da quả nh&oacute;t.</p><p>Chiều nắng t&agrave;n, m&aacute;t dịu. Biển xanh trong veo m&agrave;u mảnh chai. Những con s&oacute;ng nh&egrave; nhẹ liếm tr&ecirc;n b&atilde;i c&aacute;t, bọt s&oacute;ng m&agrave;u bưởi đ&agrave;o.</p>', 1, 22, 2, 30, NULL);
