INSERT INTO "LessonVersion" (id, article, createdate, creatorid, description, grammar, lessonid, listening, practice, reading, state, title, updatedate, version, vocab) VALUES (26, '<p>　　　　　　　　　　　　　　　　　<strong>T&ocirc;i học tiếng Việt</strong><strong><img class="fr-dib fr-draggable" src="http://localhost:8080/Veazy/res/upload/4/20160830/20150729211514-man-reading-book-hipster.jpeg" style="width: 748px; height: 346.274px;"></strong><br></p><p>&nbsp;T&ocirc;i l&agrave; Tanaka, t&ocirc;i l&agrave; người Nhật. T&ocirc;i đến Việt Nam dạy tiếng Nhật v&agrave; học tiếng Việt. Hiện nay t&ocirc;i đang học tiếng Việt. Trước đ&acirc;y, t&ocirc;i l&agrave; gi&aacute;o vi&ecirc;n tiếng Nhật. T&ocirc;i sẽ học tiếng Việt một nǎm. Tiếng Việt kh&ocirc;ng kh&oacute; nhưng cũng kh&ocirc;ng dễ. T&ocirc;i đang học ph&aacute;t &acirc;m. T&ocirc;i hy vọng t&ocirc;i sẽ n&oacute;i tốt tiếng Việt. Bạn t&ocirc;i l&agrave; Takeshi. Anh ấy đ&atilde; học tiếng Việt hai nǎm. B&acirc;y giờ anh ấy rất giỏi tiếng Việt. T&ocirc;i dạy sinh vi&ecirc;n Việt Nam tiếng Nhật v&agrave; họ cũng gi&uacute;p t&ocirc;i học tiếng Việt.</p>', 1472536033768, 4, '言語や国籍についての質問', '<ul><li>1. 言葉 「<strong><em>&iacute;t</em></strong> 」： &ldquo;少しい&ldquo;と「<em><strong>nhiều</strong></em>」：&ldquo;たくさん&rdquo; 品質を示します。</li><li>&nbsp;彼らが先行してもよいです：任意の分類器なし可算名: 「&iacute;t s&aacute;ch」 :&ldquo;少しい &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;本&rdquo;、「nhiều s&aacute;ch」&ldquo;たくさん本がある&rdquo;</li><li><br></li><li>2. 言葉 「<strong><em>hay</em></strong>」：&ldquo;か&rdquo;</li><li>&nbsp;例える：Anh đọc s&aacute;ch hay kh&ocirc;ng đọc?</li><li>&nbsp;(あなたは本を読みますか。読みませんか。)</li></ul>', 3, '<p><a class="fr-file" href="https://i.froala.com/download/548131fde2b48ca7951aa9653bf1f561716aaef9.mp3?1472488682">ng&ocirc;n ngữ, quốc tịch.mp3</a></p>', '<p>ありません</p>', '<p><span style="font-size: 18px;"><strong>会話:</strong></span></p><p><strong>A(アメリカンの学生)</strong>: Ch&agrave;o anh! Anh l&agrave; người Việt, phải kh&ocirc;ng?</p><p>(はじめまして。あなたはベトナムの人でしょう。)</p><p><br></p><p><strong>B(ベトナムの学生)</strong>: Ch&agrave;o anh!　Anh biết tiếng Việt &agrave;?</p><p>(はじめまして。ベトナム語を知っていますか。)</p><p><br></p><p><strong>A</strong>: T&ocirc;i biết tiếng Việt rất &iacute;t. T&ocirc;i học tiếng Việt ở trường Đại học Todai. T&ocirc;i l&agrave; sinh vi&ecirc;n năm thứ nhất.</p><p>(ベトナム語を少しく知っています。Todai大学でベトナム語を習います。新入生です。)</p><p><br></p><p><strong>B</strong>: Ai dạy c&aacute;c anh tiếng Việt?</p><p>(だれにベトナム語を教えてもらいますか。)</p><p><br></p><p><strong>A</strong>: Một c&ocirc; gi&aacute;o người Việt. Tiếng Việt kh&oacute; lắm.</p><p>(ベトナム人の先生です。ベトナム語が難しいですよ。)</p><p><br></p><p><strong>B</strong>: Tiếng Nhật cũng rất kh&oacute;.</p><p>(日本語も難しいです。)</p><p><br></p><p><strong>A</strong>: Anh n&oacute;i tiếng Nhật giỏi lắm.</p><p>(あなたの日本語は上旬ですよ。)</p><p><br></p><p><strong>B</strong>: Anh khen t&ocirc;i qu&aacute; lời!</p><p>(褒めすぎます。)</p>', 2, '言語、国籍', NULL, 7, '<table border="1" cellpadding="0" cellspacing="0"><tbody><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>&iacute;t</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">少しい<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>qu&aacute;</strong><br></td><td valign="top" width="160">オーバー<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>năm</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">年<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>khen qu&aacute; lời</strong><br></td><td valign="top" width="160">褒めすぎる<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>thứ nhất</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">一次<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>năm nay</strong><br></td><td valign="top" width="160">今年<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>sinh vi&ecirc;n năm thứ nhất</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">新入生<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>cậu</strong><br></td><td valign="top" width="160">あんた<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>dạy</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">教える<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>ngoại ngữ</strong><br></td><td valign="top" width="160">外国<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>c&aacute;c</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">複数のマーカー<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>hay</strong><br></td><td valign="top" width="160">それとも<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>giỏi</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">旨い、よく<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>m&igrave;nh</strong><br></td><td valign="top" width="160">ぼく<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>lắm</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">とても<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>định</strong><br></td><td valign="top" width="160">つもり、よてい<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>khen</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">褒める<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>thế n&agrave;o?</strong><br></td><td valign="top" width="160">どう<br></td></tr></tbody></table>');
INSERT INTO "LessonVersion" (id, article, createdate, creatorid, description, grammar, lessonid, listening, practice, reading, state, title, updatedate, version, vocab) VALUES (27, '<p style="text-align: center;"><strong><span style="font-size: 18px;">Giới thiệu</span></strong></p><p style="text-align: center;"><img class="fr-dib fr-draggable" src="http://localhost:8080/Veazy/res/upload/4/20160830/introduce-yourself-1-728.jpg" style="width: 305px; height: 228.75px;"></p><p>1. T&ocirc;i l&agrave; sinh vi&ecirc;n. Chị Ikeda v&agrave; anh Tanaka cũng l&agrave; sinh vi&ecirc;n. Thầy Nh&acirc;n l&agrave; thầy gi&aacute;o của ch&uacute;ng t&ocirc;i.</p><p>&nbsp;Lớp học của ch&uacute;ng t&ocirc;i ở đ&acirc;y. Kia l&agrave; ph&ograve;ng của &ocirc;ng Chủ nhiệm khoa. Đ&oacute; l&agrave; thư viện, c&ograve;n ph&ograve;ng vǎn thư ở kia.</p><p><br></p><p>2. &ocirc;ng ấy l&agrave; gi&aacute;o vi&ecirc;n. &ocirc;ng ấy kh&ocirc;ng phải l&agrave; chủ nhiệm khoa. Gi&aacute;o sư Phương l&agrave; chủ nhiệm khoa.</p><p>Đ&acirc;y l&agrave; ph&ograve;ng ngữ &acirc;m. Đ&acirc;y l&agrave; c&aacute;i m&aacute;y ghi &acirc;m. M&aacute;y ghi &acirc;m ấy của anh shiba. M&aacute;y ghi &acirc;m ấy kh&ocirc;ng phải của t&ocirc;i.</p><p><br></p><p>3. Anh Nǎm l&agrave; b&aacute;c sĩ, t&ocirc;i cũng l&agrave; b&aacute;c sĩ. Vợ của anh Nǎm cũng l&agrave; b&aacute;c sĩ. Vợ của t&ocirc;i kh&ocirc;ng phải l&agrave; b&aacute;c sĩ. Vợ t&ocirc;i l&agrave; kỹ sư.</p><p><br></p><p>4. Chị H&agrave; kh&ocirc;ng phải l&agrave; nh&acirc;n vi&ecirc;n tiếp t&acirc;n. Chị Lan l&agrave; nh&acirc;n vi&ecirc;n vǎn thư. Họ kh&ocirc;ng phải l&agrave; nh&acirc;n vi&ecirc;n tiếp t&acirc;n.</p><p><br></p><p>5. Đ&acirc;y l&agrave; c&aacute;i b&uacute;t bi. Đ&acirc;y kh&ocirc;ng phải l&agrave; c&aacute;i b&uacute;t mực. Đ&acirc;y l&agrave; quyển s&aacute;ch, kh&ocirc;ng phải l&agrave; quyển vở.</p>', 1472536158609, 4, '紹介について連勝しましょう。', '<p>1.「<em><strong>đ&atilde;</strong></em>」：「Ｖ～ました」：過去に発生したアクション</p><p>例：Anh ấy đ&atilde; ăn trưa.</p><p>(彼はご飯をたべました。)</p><p><br></p><p>2. 「<strong><em>đang</em></strong>」：「Vています」：現在進行中のアクション</p><p>例：C&ocirc; ấy đang ngủ.</p><p>(彼女は寝ています。)</p>', 4, '<p><a class="fr-file" href="https://i.froala.com/download/66e9288187eda1cd24106bc618f02fead8b507b6.mp3?1472486723">giới thiệu.mp3</a></p>', '<p>ありません</p>', '<p><strong>Th&agrave;nh</strong>: Ch&agrave;o c&ocirc;! T&ocirc;i t&ecirc;n l&agrave; Th&agrave;nh. C&ograve;n c&ocirc; c&oacute; phải l&agrave; Ikeda kh&ocirc;ng ?</p><p>（こんにちは。私はThanhです。お名前は池田ですか。）</p><p><strong>Ikeda</strong>: V&acirc;ng</p><p>（はい。）</p><p><strong>Th&agrave;nh</strong>: T&ocirc;i đ&atilde; nghe c&aacute;c bạn t&ocirc;i n&oacute;i nhiều về c&ocirc;.</p><p>（友達から、あなたについていろいろなことだということです。）</p><p><strong>Ikeda</strong>: Thế &agrave;? Họ n&oacute;i g&igrave; về t&ocirc;i đấy?</p><p>（そうですか。私について何を言いましたか。）</p><p><strong>Th&agrave;nh</strong>: Họ n&oacute;i: C&ocirc; biết tiếng Việt rất kh&aacute;, đ&atilde; đi Việt Nam thực tập 1 năm, đang l&agrave;m việc cho 1 c&ocirc;ng ty văn ph&ograve;ng ở H&agrave; Nội, sắp bảo vệ luận &aacute;n tiến sĩ, sau đ&oacute; sẽ sang Việt Nam l&agrave;m việc. V&agrave; chưa c&oacute; gia đ&igrave;nh.</p><p>（みんなさんベトナム語がよくて、ベトナムへ１年実習しに行って、HaNoiで事務所の会社に働いていて、博士論文の防衛をすることになって、それからベトナムへはたらきに行って、まだ結婚しないと言いました。）</p><p><strong>Ikeda</strong>: C&aacute;c bạn anh biết về t&ocirc;i nhiều qu&aacute;!</p><p>（あなたの友達は私についてたくさん知っていますね。）</p>', 2, '紹介', NULL, 4, '<table border="1" cellpadding="0" cellspacing="0"><tbody><tr><td style="width: 27.6898%;" valign="top" width="175"><strong>Giới thiệu</strong><br></td><td style="width: 25.2468%;" valign="top" width="144">紹介<br></td><td style="width: 21.8216%;" valign="top" width="160"><strong>lịch sử</strong><br></td><td valign="top" width="160">歴史<br></td></tr><tr><td style="width: 27.6898%;" valign="top" width="175"><strong>n&oacute;i<br></strong></td><td style="width: 25.2468%;" valign="top" width="144">いう<br></td><td style="width: 21.8216%;" valign="top" width="160"><strong>nghe</strong><br></td><td valign="top" width="160">聞く<br></td></tr><tr><td style="width: 27.6898%;" valign="top" width="175"><strong>đại học</strong><br></td><td style="width: 25.2468%;" valign="top" width="144">大学で<br></td><td style="width: 21.8216%;" valign="top" width="160"><strong>đấy</strong><br></td><td valign="top" width="160">その<br></td></tr><tr><td style="width: 27.6898%;" valign="top" width="175"><strong>to&aacute;n</strong><br></td><td style="width: 25.2468%;" valign="top" width="144">数学<br></td><td style="width: 21.8216%;" valign="top" width="160"><strong>thực tập</strong><br></td><td valign="top" width="160">実習<br></td></tr><tr><td style="width: 27.6898%;" valign="top" width="175"><strong>h&acirc;n hạnh l&agrave;m quen, mong</strong> <strong>mọi người gi&uacute;p đỡ</strong><br></td><td style="width: 25.2468%;" valign="top" width="144">よろしくお願いします<br></td><td style="width: 21.8216%;" valign="top" width="160"><strong>c&ocirc;ng ty</strong><br></td><td valign="top" width="160">会社<br></td></tr><tr><td style="width: 27.6898%;" valign="top" width="175"><strong>c&aacute;ch đ&acirc;y</strong><br></td><td style="width: 25.2468%;" valign="top" width="144">まえに<br></td><td style="width: 21.8216%;" valign="top" width="160"><strong>sắp</strong><br></td><td valign="top" width="160">そう<br></td></tr><tr><td style="width: 27.6898%;" valign="top" width="175"><strong>c&aacute;ch đ&acirc;y 3 năm</strong><br></td><td style="width: 25.2468%;" valign="top" width="144">３年なえに<br></td><td style="width: 21.8216%;" valign="top" width="160"><strong>chưa</strong><br></td><td valign="top" width="160">まだ<br></td></tr><tr><td style="width: 27.6898%;" valign="top" width="175"><strong>gia đ&igrave;nh</strong><br></td><td style="width: 25.2468%;" valign="top" width="144">家族<br></td><td style="width: 21.8216%;" valign="top" width="160"><strong>tiến sĩ</strong><br></td><td valign="top" width="160">博士<br></td></tr><tr><td style="width: 27.6898%;" valign="top" width="175"><strong>văn ph&ograve;ng</strong><br></td><td style="width: 25.2468%;" valign="top" width="144">事務所<br></td><td style="width: 21.8216%;" valign="top" width="160"><strong>luận &aacute;n tiến sĩ</strong><br></td><td valign="top" width="160">博士論文<br></td></tr></tbody></table>');
INSERT INTO "LessonVersion" (id, article, createdate, creatorid, description, grammar, lessonid, listening, practice, reading, state, title, updatedate, version, vocab) VALUES (28, '<p>　　　　　　　　　　　　　　　　　<strong>T&ocirc;i học tiếng Việt</strong></p><p style="text-align: center;"><strong><img class="fr-dib fr-draggable" src="http://localhost:8080/Veazy/res/upload/4/20160830/20150729211514-man-reading-book-hipster.jpeg" style="width: 402px; height: 226.46px;"></strong><br></p><p>&nbsp;T&ocirc;i l&agrave; Tanaka, t&ocirc;i l&agrave; người Nhật. T&ocirc;i đến Việt Nam dạy tiếng Nhật v&agrave; học tiếng Việt. Hiện nay t&ocirc;i đang học tiếng Việt. Trước đ&acirc;y, t&ocirc;i l&agrave; gi&aacute;o vi&ecirc;n tiếng Nhật. T&ocirc;i sẽ học tiếng Việt một nǎm. Tiếng Việt kh&ocirc;ng kh&oacute; nhưng cũng kh&ocirc;ng dễ. T&ocirc;i đang học ph&aacute;t &acirc;m. T&ocirc;i hy vọng t&ocirc;i sẽ n&oacute;i tốt tiếng Việt. Bạn t&ocirc;i l&agrave; Takeshi. Anh ấy đ&atilde; học tiếng Việt hai nǎm. B&acirc;y giờ anh ấy rất giỏi tiếng Việt. T&ocirc;i dạy sinh vi&ecirc;n Việt Nam tiếng Nhật v&agrave; họ cũng gi&uacute;p t&ocirc;i học tiếng Việt.</p>', 1472536248329, 4, '言語や国籍についての質問', '<ul><li>1. 言葉 「<strong><em>&iacute;t</em></strong> 」： &ldquo;少しい&ldquo;と「<em><strong>nhiều</strong></em>」：&ldquo;たくさん&rdquo; 品質を示します。</li><li>&nbsp;彼らが先行してもよいです：任意の分類器なし可算名: 「&iacute;t s&aacute;ch」 :&ldquo;少しい &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;本&rdquo;、「nhiều s&aacute;ch」&ldquo;たくさん本がある&rdquo;</li><li><br></li><li>2. 言葉 「<strong><em>hay</em></strong>」：&ldquo;か&rdquo;</li><li>&nbsp;例える：Anh đọc s&aacute;ch hay kh&ocirc;ng đọc?</li><li>&nbsp;(あなたは本を読みますか。読みませんか。)</li></ul>', 3, '<p><a class="fr-file" href="https://i.froala.com/download/548131fde2b48ca7951aa9653bf1f561716aaef9.mp3?1472488682">ng&ocirc;n ngữ, quốc tịch.mp3</a></p>', '<p>ありません</p>', '<p><span style="font-size: 18px;"><strong>会話:</strong></span></p><p><strong>A(アメリカンの学生)</strong>: Ch&agrave;o anh! Anh l&agrave; người Việt, phải kh&ocirc;ng?</p><p>(はじめまして。あなたはベトナムの人でしょう。)</p><p><br></p><p><strong>B(ベトナムの学生)</strong>: Ch&agrave;o anh!　Anh biết tiếng Việt &agrave;?</p><p>(はじめまして。ベトナム語を知っていますか。)</p><p><br></p><p><strong>A</strong>: T&ocirc;i biết tiếng Việt rất &iacute;t. T&ocirc;i học tiếng Việt ở trường Đại học Todai. T&ocirc;i l&agrave; sinh vi&ecirc;n năm thứ nhất.</p><p>(ベトナム語を少しく知っています。Todai大学でベトナム語を習います。新入生です。)</p><p><br></p><p><strong>B</strong>: Ai dạy c&aacute;c anh tiếng Việt?</p><p>(だれにベトナム語を教えてもらいますか。)</p><p><br></p><p><strong>A</strong>: Một c&ocirc; gi&aacute;o người Việt. Tiếng Việt kh&oacute; lắm.</p><p>(ベトナム人の先生です。ベトナム語が難しいですよ。)</p><p><br></p><p><strong>B</strong>: Tiếng Nhật cũng rất kh&oacute;.</p><p>(日本語も難しいです。)</p><p><br></p><p><strong>A</strong>: Anh n&oacute;i tiếng Nhật giỏi lắm.</p><p>(あなたの日本語は上旬ですよ。)</p><p><br></p><p><strong>B</strong>: Anh khen t&ocirc;i qu&aacute; lời!</p><p>(褒めすぎます。)</p>', 2, '言語、国籍', NULL, 8, '<table border="1" cellpadding="0" cellspacing="0"><tbody><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>&iacute;t</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">少しい<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>qu&aacute;</strong><br></td><td valign="top" width="160">オーバー<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>năm</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">年<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>khen qu&aacute; lời</strong><br></td><td valign="top" width="160">褒めすぎる<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>thứ nhất</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">一次<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>năm nay</strong><br></td><td valign="top" width="160">今年<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>sinh vi&ecirc;n năm thứ nhất</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">新入生<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>cậu</strong><br></td><td valign="top" width="160">あんた<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>dạy</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">教える<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>ngoại ngữ</strong><br></td><td valign="top" width="160">外国<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>c&aacute;c</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">複数のマーカー<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>hay</strong><br></td><td valign="top" width="160">それとも<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>giỏi</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">旨い、よく<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>m&igrave;nh</strong><br></td><td valign="top" width="160">ぼく<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>lắm</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">とても<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>định</strong><br></td><td valign="top" width="160">つもり、よてい<br></td></tr><tr><td style="width: 24.7582%;" valign="top" width="160"><strong>khen</strong><br></td><td style="width: 24.9211%;" valign="top" width="160">褒める<br></td><td style="width: 24.916%;" valign="top" width="160"><strong>thế n&agrave;o?</strong><br></td><td valign="top" width="160">どう<br></td></tr></tbody></table>');
