INSERT INTO "Question" (id, createdate, deletedate, deleteflag, updatedate, attachment, courseid, creatorid, numberofquestion, question, questionanswertype, questioncode, questionetatime, questionskill, questiontype, parentquestion_id) VALUES (11, NULL, NULL, false, NULL, NULL, 4, 4, 5, '<p>Rừng c&acirc;y im lặng qu&aacute;. Một tiếng l&aacute; rơi l&uacute;c n&agrave;y cũng c&oacute; thể khiến người ta giật m&igrave;nh. Lạ qu&aacute;, chim ch&oacute;c chẳng nghe con n&agrave;o k&ecirc;u. Hay vừa c&oacute; tiếng chim ở một nơi n&agrave;o xa lắm, v&igrave; kh&ocirc;ng ch&uacute; &yacute; m&agrave; t&ocirc;i kh&ocirc;ng nghe chăng.Gi&oacute; bắt đầu nổi r&agrave;o r&agrave;o theo với khối mặt trời đang tu&ocirc;n s&aacute;ng v&agrave;ng rực xuống mặt đất. Một l&agrave;n hơi đất nh&egrave; nhẹ tỏa l&ecirc;n, phủ mờ những c&acirc;y c&uacute;c &aacute;o, rồi tan biến theo hơi ấm mặt trời. Ph&uacute;t y&ecirc;n tĩnh của rừng ban mai dần dần biến đi.Chim h&oacute;t l&iacute;u lo. Nắng bốc hương hoa tr&agrave;m thơm ng&acirc;y ngất. Gi&oacute; đưa m&ugrave;i hương ngọt lan xa, phảng phất khắp rừng. Mấy con kỳ nh&ocirc;ng nằm phơi lưng tr&ecirc;n gốc c&acirc;y mục, sắc da lưng lu&ocirc;n biến đổi từ xanh h&oacute;a v&agrave;ng, từ v&agrave;ng h&oacute;a đỏ, từ đỏ h&oacute;a t&iacute;m xanh&hellip;Con luốc động đậy c&aacute;nh mũi, r&oacute;n r&eacute;n b&ograve; tới. Nghe tiếng ch&acirc;n con ch&oacute; săn nguy hiểm, những con vật thuộc lo&agrave;i b&ograve; s&aacute;t c&oacute; bốn ch&acirc;n to hơn ng&oacute;n ch&acirc;n c&aacute;i kia liền qu&eacute;t chiếc đu&ocirc;i d&agrave;i chạy tứ t&aacute;n, con nấp chỗ gốc c&acirc;y th&igrave; biến th&agrave;nh m&agrave;u x&aacute;m vỏ c&acirc;y, con đeo tr&ecirc;n t&aacute;n l&aacute; ng&aacute;i th&igrave; biến ra m&agrave;u xanh l&aacute; ng&aacute;i.</p>', 1, 11, 330, 4, 2, NULL);
